#include <stdio.h>

int gcd(int a, int b) {
    if (b == 0) {
        return a;
    }
    return gcd(b, a % b);
}

int main() {
    int A, B, C;
    scanf("%d %d %d", &A, &B, &C);
    int g = gcd(A, B);
    if (C % g == 0) {
        printf("YES\n");
    } else {
        printf("NO\n");
    }
    return 0;
}
