import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long a = sc.nextLong();
        long b = sc.nextLong();
        long c = sc.nextLong();
        if (a > b && c != 0) {
            System.out.println("NO");
            return;
        }
        long gcd = gcd(a, b);
        if (c % gcd != 0) {
            System.out.println("NO");
            return;
        }
        System.out.println("YES");
    }

    static long gcd(long a, long b) {
        if (b == 0) {
            return a;
        }
        return gcd(b, a % b);
    }
}
