#include <stdio.h>

int main() {
    long long N, T;
    scanf("%lld %lld", &N, &T);
    long long t[N];
    for (long long i = 0; i < N; i++) {
        scanf("%lld", &t[i]);
    }
    long long total_time = 0;
    long long end_time = t[0] + T;
    total_time += T;
    for (long long i = 1; i < N; i++) {
        if (t[i] < end_time) {
            end_time = t[i] + T;
        } else {
            total_time += T;
            end_time = t[i] + T;
        }
    }
    printf("%lld\n", total_time);
    return 0;
}
