#include <iostream>

int main() {
    long long N, T;
    std::cin >> N >> T;
    long long t[N];
    for (long long i = 0; i < N; ++i) {
        std::cin >> t[i];
    }
    long long total_time = 0;
    long long end_time = t[0] + T;
    total_time = end_time;
    for (long long i = 1; i < N; ++i) {
        if (t[i] < end_time) {
            end_time = t[i] + T;
        } else {
            total_time += T;
            end_time = t[i] + T;
        }
    }
    std::cout << total_time << std::endl;
    return 0;
}
