#include <stdio.h>
#include <string.h>

int main() {
    char s[100001];
    scanf("%s", s);
    int n = strlen(s);
    int l = 0, r = n - 1;
    int count = 0;
    while (l < r) {
        if (s[l] == s[r]) {
            l++;
            r--;
        } else {
            count++;
            l++;
        }
    }
    int diff = 0;
    l = 0, r = n - 1;
    while (l < r) {
        if (s[l] != s[r])
            diff++;
        l++;
        r--;
    }
    if (diff == 0)
        printf("0\n");
    else if (diff % 2 == 0)
        printf("%d\n", diff);
    else
        printf("-1\n");
    return 0;
}
