#include <stdio.h>

int main() {
    int N, W;
    scanf("%d %d", &N, &W);
    long long w[N], v[N];
    for (int i = 0; i < N; i++) {
        scanf("%lld %lld", &w[i], &v[i]);
    }
    long long dp[W + 1];
    for (int i = 0; i <= W; i++) {
        dp[i] = 0;
    }
    for (int i = 0; i < N; i++) {
        for (int j = W; j >= w[i]; j--) {
            dp[j] = (dp[j] > dp[j - w[i]] + v[i]) ? dp[j] : dp[j - w[i]] + v[i];
        }
    }
    printf("%lld\n", dp[W]);
    return 0;
}
