#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, w;
  cin >> n >> w;

  vector<pair<long long, long long>> items(n);
  for (int i = 0; i < n; ++i) {
    cin >> items[i].first >> items[i].second;
  }

  long long max_value = 0;
  for (int i = 0; i < (1 << n); ++i) {
    long long current_weight = 0;
    long long current_value = 0;
    for (int j = 0; j < n; ++j) {
      if ((i >> j) & 1) {
        current_weight += items[j].first;
        current_value += items[j].second;
      }
    }
    if (current_weight <= w) {
      max_value = max(max_value, current_value);
    }
  }
  cout << max_value << endl;
  return 0;
}
