import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long w = sc.nextLong();
        long[][] items = new long[n][2];
        for (int i = 0; i < n; i++) {
            items[i][0] = sc.nextLong();
            items[i][1] = sc.nextLong();
        }
        Arrays.sort(items, (a, b) -> Long.compare(a[0], b[0]));
        long[] dp = new long[100001];
        for (int i = 0; i < n; i++) {
            long weight = items[i][0];
            long value = items[i][1];
            for (int j = 100000; j >= (int) weight; j--) {
                dp[j] = Math.max(dp[j], dp[(int) (j - weight)] + value);
            }
        }
        long max_value = 0;
        for (int i = 0; i <= 100000; i++) {
            max_value = Math.max(max_value, dp[i]);
        }
        System.out.println(max_value);
    }
}
