#include <stdio.h>

int main() {
    int N, W;
    scanf("%d %d", &N, &W);
    long long w[N], v[N];
    for (int i = 0; i < N; i++) {
        scanf("%lld %lld", &w[i], &v[i]);
    }
    long long dp[N + 1][W + 1];
    for (int i = 0; i <= N; i++) {
        for (int j = 0; j <= W; j++) {
            dp[i][j] = 0;
        }
    }
    for (int i = 1; i <= N; i++) {
        for (int j = 0; j <= W; j++) {
            if (j >= w[i - 1]) {
                dp[i][j] = (dp[i - 1][j] > dp[i - 1][j - w[i - 1]] + v[i - 1] ? dp[i - 1][j] : dp[i - 1][j - w[i - 1]] + v[i - 1]);
            } else {
                dp[i][j] = dp[i - 1][j];
            }
        }
    }
    printf("%lld\n", dp[N][W]);
    return 0;
}
