import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long w = sc.nextLong();
        long[] weight = new long[n];
        long[] value = new long[n];
        for (int i = 0; i < n; i++) {
            weight[i] = sc.nextLong();
            value[i] = sc.nextLong();
        }
        long[][] dp = new long[n + 1][(int) w + 1];
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= w; j++) {
                dp[i][j] = dp[i - 1][j];
                if (j >= weight[i - 1]) {
                    dp[i][j] = Math.max(dp[i][j], dp[i - 1][(int) (j - weight[i - 1])] + value[i - 1]);
                }
            }
        }
        System.out.println(dp[n][(int) w]);
    }
}
