#include <iostream>
#include <string>
#include <algorithm>

using namespace std;

int main() {
  string s;
  cin >> s;

  string rs = s;
  reverse(rs.begin(), rs.end());

  int n = s.length();
  int count = 0;
  int i = 0, j = 0;
  while (i < n && j < n) {
    if (s[i] == rs[j]) {
      i++;
      j++;
    } else {
      count++;
      j++;
    }
  }
  count += (n - i);

  string t = s;
  reverse(t.begin(),t.end());
  if(t==s) cout << 0 << endl;
  else if(count %2 ==0) cout << count << endl;
  else cout << -1 << endl;

  return 0;
}
