N, W = map(int, input().split())
items = []
for i in range(N):
    w, v = map(int, input().split())
    items.append((w, v))

dp = {}
dp[0] = 0

for w, v in items:
    new_dp = {}
    for weight, value in dp.items():
        if weight + w <= W:
            new_dp[weight + w] = max(new_dp.get(weight + w, 0), value + v)
        new_dp[weight] = max(new_dp.get(weight, 0), value)
    dp.update(new_dp)

print(max(dp.values()))

