#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

long long min(long long a, long long b) {
    return a < b ? a : b;
}

int main() {
    int n;
    scanf("%d", &n);
    long long x[n], y[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld %lld", &x[i], &y[i]);
    }
    long long min_val = LLONG_MAX;
    for (int i = 0; i < (1 << n); i++) {
        long long red_max = LLONG_MIN;
        long long red_min = LLONG_MAX;
        long long blue_max = LLONG_MIN;
        long long blue_min = LLONG_MAX;
        for (int j = 0; j < n; j++) {
            if ((i >> j) & 1) {
                red_max = red_max > x[j] ? red_max : x[j];
                red_min = red_min < x[j] ? red_min : x[j];
                blue_max = blue_max > y[j] ? blue_max : y[j];
                blue_min = blue_min < y[j] ? blue_min : y[j];
            } else {
                red_max = red_max > y[j] ? red_max : y[j];
                red_min = red_min < y[j] ? red_min : y[j];
                blue_max = blue_max > x[j] ? blue_max : x[j];
                blue_min = blue_min < x[j] ? blue_min : x[j];
            }
        }
        if (red_min == LLONG_MAX || blue_min == LLONG_MAX) continue;
        min_val = min(min_val, (red_max - red_min) * (blue_max - blue_min));
    }
    printf("%lld\n", min_val);
    return 0;
}
