#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<pair<long long, long long>> bags(n);
  for (int i = 0; i < n; ++i) {
    cin >> bags[i].first >> bags[i].second;
  }

  long long min_prod = -1;

  for (int i = 0; i < (1 << n); ++i) {
    vector<long long> red;
    vector<long long> blue;
    for (int j = 0; j < n; ++j) {
      if ((i >> j) & 1) {
        red.push_back(bags[j].first);
        blue.push_back(bags[j].second);
      } else {
        red.push_back(bags[j].second);
        blue.push_back(bags[j].first);
      }
    }

    long long r_max = *max_element(red.begin(), red.end());
    long long r_min = *min_element(red.begin(), red.end());
    long long b_max = *max_element(blue.begin(), blue.end());
    long long b_min = *min_element(blue.begin(), blue.end());

    long long prod = (r_max - r_min) * (b_max - b_min);
    if (min_prod == -1 || prod < min_prod) {
      min_prod = prod;
    }
  }

  cout << min_prod << endl;
  return 0;
}
