import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[][] bags = new long[n][2];
        for (int i = 0; i < n; i++) {
            bags[i][0] = sc.nextLong();
            bags[i][1] = sc.nextLong();
        }
        long minProduct = Long.MAX_VALUE;
        for (int i = 0; i < (1 << n); i++) {
            List<Long> red = new ArrayList<>();
            List<Long> blue = new ArrayList<>();
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 0) {
                    red.add(bags[j][0]);
                    blue.add(bags[j][1]);
                } else {
                    red.add(bags[j][1]);
                    blue.add(bags[j][0]);
                }
            }
            Collections.sort(red);
            Collections.sort(blue);
            long rmax = red.get(red.size() - 1);
            long rmin = red.get(0);
            long bmax = blue.get(blue.size() - 1);
            long bmin = blue.get(0);
            minProduct = Math.min(minProduct, (rmax - rmin) * (bmax - bmin));
        }
        System.out.println(minProduct);
        sc.close();
    }
}
