#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  long long n, q, a, b;
  cin >> n >> q >> a >> b;

  vector<long long> x(q);
  for (int i = 0; i < q; ++i) {
    cin >> x[i];
  }

  long long min_time = -1;

  for (int i = 0; i < (1 << q); ++i) {
    long long current_time = 0;
    long long current_a = a;
    long long current_b = b;
    long long current_x_index = 0;

    for (int j = 0; j < q; ++j) {
      long long target = x[j];
      long long move_a_time = abs(current_a - target);
      long long move_b_time = abs(current_b - target);

      if ((i >> j) & 1) { 
        current_time += move_a_time;
        current_a = target;
      } else { 
        current_time += move_b_time;
        current_b = target;
      }
    }

    if (min_time == -1 || current_time < min_time) {
      min_time = current_time;
    }
  }
  cout << min_time << endl;
  return 0;
}
