import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long q = sc.nextLong();
        long a = sc.nextLong();
        long b = sc.nextLong();
        long[] x = new long[(int) q];
        for (int i = 0; i < q; i++) {
            x[i] = sc.nextLong();
        }
        long ans = 0;
        long curA = a;
        long curB = b;
        for (int i = 0; i < q; i++) {
            long distA = Math.abs(curA - x[i]);
            long distB = Math.abs(curB - x[i]);
            if (distA <= distB) {
                ans += distA;
                curA = x[i];
            } else {
                ans += distB;
                curB = x[i];
            }
        }
        System.out.println(ans);
    }
}
