#include <stdio.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);
    long long a[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld", &a[i]);
    }
    long long sum = 0;
    long long ans = 0;
    long long cur_sum = 0;
    for (int i = 0; i < n; i++) {
        cur_sum += a[i];
        if (i == 0) {
            if (cur_sum == 0) {
                ans++;
                cur_sum++;
            }
        } else {
            if (cur_sum == 0) {
                ans++;
                cur_sum++;
            } else if ((cur_sum > 0 && sum >= 0) || (cur_sum < 0 && sum <= 0)) {
                ans += abs(cur_sum);
                cur_sum = -sum;

            }
        }
        sum = cur_sum;
    }
    printf("%lld\n", ans);
    return 0;
}
