#include <iostream>
#include <vector>
#include <numeric>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  long long ans = 0;
  vector<long long> sum(n + 1, 0);
  for (int i = 0; i < n; ++i) {
    sum[i + 1] = sum[i] + a[i];
  }

  for (int i = 1; i < n; ++i) {
    if (sum[i] == 0) {
      ans++;
      sum[i + 1]++;
      sum[i] = 1;

    } else if ((sum[i] > 0 && sum[i + 1] > 0) || (sum[i] < 0 && sum[i + 1] < 0)) {
      long long diff = abs(sum[i]);
      ans += diff + 1;
      sum[i+1] += (sum[i] > 0 ? -(diff+1) : diff+1);

    }
  }


  cout << ans << endl;
  return 0;
}
