import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        long sum = 0;
        long ans = 0;
        long[] s = new long[n];
        s[0] = a[0];
        if (s[0] == 0) {
            ans++;
            s[0] = 1;
        }
        for (int i = 1; i < n; i++) {
            s[i] = s[i - 1] + a[i];
            if (s[i] == 0) {
                ans++;
                s[i] = 1;
            } else if (s[i] * s[i - 1] > 0) {
                ans += Math.min(Math.abs(s[i]), Math.abs(s[i] - (s[i - 1] * 2)));
                s[i] = -s[i - 1];
            }
        }
        System.out.println(ans);
    }
}
