#include <stdio.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);

    long long a[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld", &a[i]);
    }

    long long sum = 0;
    long long operations = 0;
    long long prev_sum = 0;
    long long current_sum = 0;

    for (int i = 0; i < n; i++) {
        current_sum += a[i];
        if (i == 0) {
            if (current_sum == 0) {
                operations++;
                current_sum++;
            }
        } else {
            if (current_sum == 0) {
                operations++;
                current_sum++;
            } else if ((current_sum > 0 && prev_sum > 0) || (current_sum < 0 && prev_sum < 0)) {
                operations += abs(current_sum);
                current_sum = -prev_sum + (prev_sum > 0 ? -1 : 1);

            }
        }
        prev_sum = current_sum;
    }

    printf("%lld\n", operations);
    return 0;
}
