import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        long sum = 0;
        long ans = 0;
        long[] pref = new long[n];
        pref[0] = a[0];
        for (int i = 1; i < n; i++) {
            pref[i] = pref[i - 1] + a[i];
        }
        long cur = pref[0];
        if (cur == 0) {
            ans++;
            cur = 1;
        }
        for (int i = 1; i < n; i++) {
            if ((cur > 0 && pref[i] >= 0) || (cur < 0 && pref[i] <= 0)) {
                ans += Math.abs(pref[i] - (-cur + 1));
                cur = -cur + 1;
            } else {
                ans += Math.abs(pref[i] - (-cur -1));
                cur = -cur -1;
            }

        }
        System.out.println(ans);
    }
}
