n = int(input())
a = list(map(int, input().split()))

ans = 0
cur_sum = 0
for i in range(n):
    cur_sum += a[i]
    if i < n - 1:
        if (cur_sum > 0 and a[i+1] >=0) or (cur_sum < 0 and a[i+1] <= 0):
            if cur_sum > 0:
                ans += abs(cur_sum + 1)
                cur_sum = -1
            else:
                ans += abs(cur_sum -1)
                cur_sum = 1
    else:
        if cur_sum == 0:
            ans += 1

print(ans)
