#include <stdio.h>
#include <string.h>

int main() {
    char s[200001];
    scanf("%s", s);
    int n = strlen(s);
    int count = 0;
    int odd_count = 0;
    int i = 0;
    while (i < n) {
        odd_count = 0;
        int j = i;
        while (j < n) {
            int freq[26] = {0};
            int k = i;
            while (k <= j) {
                freq[s[k] - 'a']++;
                k++;
            }
            odd_count = 0;
            for (k = 0; k < 26; k++) {
                if (freq[k] % 2 != 0) {
                    odd_count++;
                }
            }
            if (odd_count <= 1) {
                j++;
            } else {
                break;
            }
        }
        count++;
        i = j;
    }
    printf("%d\n", count);
    return 0;
}
