#include <stdio.h>

int main() {
    long long N, D;
    scanf("%lld %lld", &N, &D);
    long long d[N];
    for (long long i = 0; i < N; i++) {
        scanf("%lld", &d[i]);
    }
    long long Q;
    scanf("%lld", &Q);
    for (long long i = 0; i < Q; i++) {
        long long q;
        scanf("%lld", &q);
        q--;
        long long current_distance = D;
        long long possible = 0;
        for (long long j = 0; j < N; j++) {
            if (j == q) continue;
            if (current_distance >= d[j]) {
                current_distance -= d[j];
            }
        }
        if (current_distance > 0) possible = 1;
        if (possible) {
            printf("YES\n");
        } else {
            printf("NO\n");
        }
    }
    return 0;
}
