#include <iostream>
#include <vector>

using namespace std;

int main() {
    long long n, d;
    cin >> n >> d;

    vector<long long> a(n);
    for (int i = 0; i < n; ++i) {
        cin >> a[i];
    }

    int q;
    cin >> q;

    for (int i = 0; i < q; ++i) {
        int index;
        cin >> index;
        index--; 

        bool possible = false;
        for (long long j = 1; j <= 1000; ++j) {
            vector<long long> b = a;
            b[index] = j;
            long long current_distance = d;
            bool reached = false;
            for (long long x : b) {
                if (current_distance <= x) {
                    break;
                }
                current_distance -= x;
                if (current_distance == 0) {
                    reached = true;
                    break;
                }
            }
            if (!reached) {
                possible = true;
                break;
            }
        }
        cout << (possible ? "YES" : "NO") << endl;
    }

    return 0;
}
