import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long d = sc.nextLong();
        long[] d_arr = new long[n];
        for (int i = 0; i < n; i++) {
            d_arr[i] = sc.nextLong();
        }
        int q = sc.nextInt();
        for (int i = 0; i < q; i++) {
            int q_i = sc.nextInt() - 1;
            boolean possible = false;
            for (long j = 1; j <= 1000; j++) {
                long[] temp_arr = Arrays.copyOf(d_arr, n);
                temp_arr[q_i] = j;
                long current_d = d;
                boolean reached = false;
                for (long num : temp_arr) {
                    if (current_d <= 0) break;
                    if (current_d >= num) {
                        current_d -= num;
                    }
                }
                if (current_d > 0) {
                    possible = true;
                    break;
                }
            }
            System.out.println(possible ? "YES" : "NO");
        }
    }
}
