#include <stdio.h>

int main() {
    int N, L;
    scanf("%d %d", &N, &L);
    double t[N], v[N];
    for (int i = 0; i < N; i++) {
        scanf("%lf %lf", &t[i], &v[i]);
    }
    double total_t = t[0];
    double total_v = v[0];
    printf("%.9lf\n", total_t);
    for (int i = 1; i < N; i++) {
        double max_t = 0;
        for (double j = 0; j <= total_v; j += 0.000001) {
            double remaining_v = total_v - j;
            double new_t = (total_t * remaining_v + t[i] * v[i]) / (remaining_v + v[i]);
            if (remaining_v + v[i] == L) {
                max_t = new_t > max_t ? new_t : max_t;
            }
        }
        printf("%.9lf\n", max_t);
        total_t = max_t;
        total_v = L;
    }
    return 0;
}

