import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        double l = sc.nextDouble();
        double[] t = new double[n];
        double[] v = new double[n];
        for (int i = 0; i < n; i++) {
            t[i] = sc.nextDouble();
            v[i] = sc.nextDouble();
        }
        double[] ans = new double[n];
        ans[0] = t[0];
        double cur_v = v[0];
        double cur_t = t[0];
        for (int i = 1; i < n; i++) {
            double max_t = 0;
            for (double j = 0; j <= cur_v; j += 0.000001) {
                double next_t = (cur_t * (cur_v - j) + t[i] * v[i]) / (cur_v - j + v[i]);
                if (cur_v - j + v[i] == l) {
                    max_t = Math.max(max_t, next_t);
                }
            }
            ans[i] = max_t;
            cur_v = l;
            cur_t = ans[i];

        }
        for (int i = 0; i < n; i++) {
            System.out.printf("%.8f\n", ans[i]);
        }
    }
}
