#include <iostream>
#include <string>
#include <map>

using namespace std;

int main() {
  string s;
  cin >> s;

  int count = 0;
  map<char, int> char_count;
  for (char c : s) {
    char_count[c]++;
  }

  for (int i = 0; i < s.length(); ++i) {
    char_count[s[i]]--;
    int odd_count = 0;
    for (auto const& [key, val] : char_count) {
      if (val % 2 != 0) {
        odd_count++;
      }
    }
    if (odd_count > 1) {
      count++;
      char_count.clear();
      for (int j = i + 1; j < s.length(); ++j) {
          char_count[s[j]]++;
      }
    }

  }
  cout << count + 1 << endl;
  return 0;
}
