#include <stdio.h>

int main() {
    int n;
    scanf("%d", &n);
    int a[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    int count = 1;
    int inc = 1, dec = 1;
    for (int i = 1; i < n; i++) {
        if (a[i] >= a[i - 1]) {
            dec = 0;
        }
        if (a[i] <= a[i - 1]) {
            inc = 0;
        }
        if (inc == 0 && dec == 0) {
            count++;
            inc = 1;
            dec = 1;
        }
    }
    printf("%d\n", count);
    return 0;
}
