#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  if (n == 0) {
    cout << 0 << endl;
    return 0;
  }

  int count = 1;
  bool increasing = true;
  bool decreasing = true;

  for (int i = 1; i < n; ++i) {
    if (a[i] < a[i - 1]) {
      increasing = false;
    }
    if (a[i] > a[i - 1]) {
      decreasing = false;
    }
    if (!increasing && !decreasing) {
      count++;
      increasing = true;
      decreasing = true;
    }
  }
  cout << count << endl;
  return 0;
}
