import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
        }
        int count = 1;
        boolean inc = true;
        boolean dec = true;
        for (int i = 1; i < n; i++) {
            if (a[i] >= a[i - 1]) {
                dec = false;
            }
            if (a[i] <= a[i - 1]) {
                inc = false;
            }
            if (!inc && !dec) {
                count++;
                inc = true;
                dec = true;
            }
        }
        System.out.println(count);
    }
}
