#include <stdio.h>
#include <stdlib.h>

int main() {
    int n, m;
    scanf("%d %d", &n, &m);
    int adj[n + 1][n + 1];
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            adj[i][j] = 0;
        }
    }
    for (int i = 0; i < m; i++) {
        int u, v;
        scanf("%d %d", &u, &v);
        adj[u][v] = adj[v][u] = 1;
    }
    int path[n + 1];
    int path_len = 0;
    int visited[n + 1];
    for (int i = 1; i <= n; i++) visited[i] = 0;
    int start_node = 1;
    path[path_len++] = start_node;
    visited[start_node] = 1;
    int curr = start_node;
    while (1) {
        int next = -1;
        for (int i = 1; i <= n; i++) {
            if (adj[curr][i] && !visited[i]) {
                next = i;
                break;
            }
        }
        if (next == -1) break;
        path[path_len++] = next;
        visited[next] = 1;
        curr = next;
    }
    printf("%d\n", path_len);
    for (int i = 0; i < path_len; i++) {
        printf("%d ", path[i]);
    }
    printf("\n");
    return 0;
}
