import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        List<List<Integer>> adj = new ArrayList<>();
        for (int i = 0; i <= n; i++) {
            adj.add(new ArrayList<>());
        }
        for (int i = 0; i < m; i++) {
            int u = sc.nextInt();
            int v = sc.nextInt();
            adj.get(u).add(v);
            adj.get(v).add(u);
        }
        Set<Integer> visited = new HashSet<>();
        List<Integer> path = new ArrayList<>();
        Queue<Integer> q = new LinkedList<>();
        q.offer(1);
        visited.add(1);
        path.add(1);
        while (!q.isEmpty()) {
            int u = q.poll();
            boolean flag = true;
            for (int v : adj.get(u)) {
                if (!visited.contains(v)) {
                    visited.add(v);
                    path.add(v);
                    q.offer(v);
                    flag = false;
                    break;
                }
            }
            if (flag) {
                break;
            }
        }
        System.out.println(path.size());
        for (int i = 0; i < path.size(); i++) {
            System.out.print(path.get(i) + (i == path.size() - 1 ? "" : " "));
        }
        System.out.println();
    }
}
