#include <stdio.h>

int main() {
    long long N, L, T;
    scanf("%lld %lld %lld", &N, &L, &T);
    long long X[N], W[N];
    for (long long i = 0; i < N; i++) {
        scanf("%lld %lld", &X[i], &W[i]);
    }
    long long pos[N];
    for (long long i = 0; i < N; i++) {
        long long cur_pos = X[i];
        long long dir = W[i];
        long long moved = 0;
        while (moved < T) {
            long long move = T - moved;
            long long next_pos = (cur_pos + (dir == 1 ? move : -move) ) % L;
            if (next_pos < 0) next_pos += L;
            pos[i] = next_pos;
            moved = T;
        }
    }
    for (long long i = 0; i < N; i++) {
        printf("%lld\n", pos[i]);
    }
    return 0;
}
