import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String s = sc.next();
        int count = 0;
        int n = s.length();
        int[] freq = new int[26];
        for (int i = 0; i < n; i++) {
            freq[s.charAt(i) - 'a']++;
            int oddCount = 0;
            for (int j = 0; j < 26; j++) {
                if (freq[j] % 2 != 0) {
                    oddCount++;
                }
            }
            if (oddCount <= 1) {
                continue;
            } else {
                count++;
                Arrays.fill(freq, 0);
                freq[s.charAt(i) - 'a'] = 1;
            }
        }
        System.out.println(count + 1);
    }
}
