#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, l, t;
  cin >> n >> l >> t;

  vector<pair<long long, long long>> ants(n);
  for (int i = 0; i < n; ++i) {
    cin >> ants[i].first >> ants[i].second;
  }

  vector<long long> final_pos(n);
  for (int i = 0; i < n; ++i) {
    long long pos = ants[i].first;
    long long dir = ants[i].second;
    long long movement = dir == 1 ? t : -t;
    pos = (pos + movement) % l;
    if (pos < 0) pos += l;
    final_pos[i] = pos;
  }

  for (int i = 0; i < n; ++i) {
    cout << final_pos[i] << endl;
  }

  return 0;
}
