import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long l = sc.nextLong();
        long t = sc.nextLong();
        long[] x = new long[n];
        int[] w = new int[n];
        for (int i = 0; i < n; i++) {
            x[i] = sc.nextLong();
            w[i] = sc.nextInt();
        }
        long[] pos = new long[n];
        for (int i = 0; i < n; i++) {
            pos[i] = x[i] + (w[i] == 1 ? t : -t);
            pos[i] = (pos[i] % l + l) % l;
        }
        Arrays.sort(pos);
        for (long p : pos) {
            System.out.println(p);
        }
    }
}
