#include <stdio.h>

#define MOD 1000000007

int main() {
    long long n, m;
    scanf("%lld %lld", &n, &m);
    if (n < 2 * m) {
        printf("0\n");
        return 0;
    }
    long long ans = 1;
    for (long long i = 0; i < 2 * m; i++) {
        ans = (ans * (n - i)) % MOD;
    }
    long long inv = 1;
    long long p = MOD - 2;
    long long base = 2;
    while (p > 0) {
        if (p % 2 == 1) inv = (inv * base) % MOD;
        base = (base * base) % MOD;
        p /= 2;
    }

    ans = (ans * inv) % MOD;

    long long ans2 = 1;
    for (long long i = 0; i < 2*m; ++i) {
        ans2 = (ans2 * (i+1)) % MOD;
    }

    long long inv2 = 1;
    p = MOD -2;
    base = ans2;
    while(p > 0) {
        if (p % 2 == 1) inv2 = (inv2 * base) % MOD;
        base = (base * base) % MOD;
        p /= 2;
    }

    ans = (ans * inv2) % MOD;
    printf("%lld\n", ans);
    return 0;
}
