#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, m;
  cin >> n >> m;

  long long mod = 1e9 + 7;

  if (n >= 2 * m) {
    long long ans = 1;
    for (long long i = 0; i < 2 * m; ++i) {
      ans = (ans * 2) % mod;
    }
    cout << ans << endl;
  } else {
    long long ans = 0;

    if (n == 0 && m == 0) {
        cout << 1 << endl;
        return 0;
    }

    if (n < 2 * m) {
        vector<long long> dp(n + 1, 0);
        dp[0] = 1;
        for (int i = 0; i < m; ++i) {
            vector<long long> new_dp(n + 1, 0);
            for (int j = 0; j <= n; ++j) {
                if (dp[j] == 0) continue;
                if (j >= 1) {
                    new_dp[j - 1] = (new_dp[j-1] + dp[j]) % mod;
                }
                if (j >= 2) {
                    new_dp[j - 2] = (new_dp[j - 2] + dp[j]) % mod;
                }
                new_dp[j+1] = (new_dp[j+1] + dp[j]) % mod;


            }
            dp = new_dp;
        }
        long long final_ans = 0;
        for (long long i = 0; i <= n; ++i) {
            final_ans = (final_ans + dp[i]) % mod;
        }
        cout << final_ans << endl;
    }
  }
  return 0;
}
