```python
N, M = map(int, input().split())
MOD = 10**9 + 7

dp = {}

def count_sequences(n, m, red, blue):
    if m == 0:
        return 1
    if (n, m, red, blue) in dp:
        return dp[(n, m, red, blue)]

    count = 0
    for i in range(red + 1):
        for j in range(blue + 1):
            if i + j > 0 and n - (i + j) >= 0:
                count = (count + count_sequences(n - (i + j) + 2, m - 1, red - i + 1, blue - j + 1)) % MOD

    dp[(n, m, red, blue)] = count
    return count

result = 0
for i in range(N + 1):
    result = (result + count_sequences(N, M, i, N - i)) % MOD

print(result)
