#include <stdio.h>
#include <stdlib.h>

#define MOD 1000000007

long long power(long long base, long long exp) {
    long long res = 1;
    base %= MOD;
    while (exp > 0) {
        if (exp % 2 == 1) res = (res * base) % MOD;
        base = (base * base) % MOD;
        exp /= 2;
    }
    return res;
}

int main() {
    long long n, m;
    scanf("%lld %lld", &n, &m);
    long long x[m];
    for (int i = 0; i < m; i++) {
        scanf("%lld", &x[i]);
    }
    long long dp[m + 1][n + 1];
    for (int i = 0; i <= m; i++) {
        for (int j = 0; j <= n; j++) {
            dp[i][j] = 0;
        }
    }
    dp[0][0] = 1;
    for (int i = 0; i <= m; i++) {
        for (int j = 0; j <= n; j++) {
            if (dp[i][j] == 0) continue;
            for (long long k = 1; j + k <= n; k++) {
                long long next_j = j + k;
                long long next_i = i;
                if (i < m && next_j > x[i]) next_i++;

                long long add = (dp[i][j] * power(k,2)) % MOD;
                dp[next_i][next_j] = (dp[next_i][next_j] + add) % MOD;
            }
        }
    }
    long long ans = 0;
    for (int i = 0; i <=n; i++) {
        ans = (ans + dp[m][i]) % MOD;
    }
    printf("%lld\n", ans);
    return 0;
}
