#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, m;
  cin >> n >> m;

  vector<long long> x(m);
  for (int i = 0; i < m; ++i) {
    cin >> x[i];
  }

  long long mod = 1e9 + 7;
  long long ans = 0;

  if (m == 0) {
    ans = 1;
    for (long long i = 0; i < n; ++i) ans = (ans * n) % mod;
  } else {
      vector<long long> cuts;
      cuts.push_back(0);
      for (long long i = 0; i < m; i++) cuts.push_back(x[i]);
      cuts.push_back(n);

      long long num_segments = cuts.size() -1;
      vector<long long> dp(num_segments + 1, 0);
      dp[0] = 1;

      for(long long i = 1; i <= num_segments; ++i){
          long long cur_seg = cuts[i] - cuts[i-1];
          for(long long j = 1; j <= cur_seg; ++j){
              if (i - j >= 0)
                  dp[i] = (dp[i] + dp[i-j]) % mod;
          }
      }
      ans = dp[num_segments];
      for(long long i = 0; i < num_segments; ++i){
          long long len = cuts[i+1] - cuts[i];
          ans = (ans * len * len) % mod;
      }
  }

  cout << ans << endl;
  return 0;
}
