import java.util.*;

public class Main {
    static final int MOD = 1000000007;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        int m = sc.nextInt();
        long[] x = new long[m];
        for (int i = 0; i < m; i++) {
            x[i] = sc.nextLong();
        }
        long[] marks = new long[m + 2];
        marks[0] = 0;
        for (int i = 0; i < m; i++) {
            marks[i + 1] = x[i];
        }
        marks[m + 1] = n;
        long[] dp = new long[m + 2];
        Arrays.fill(dp, 0);
        dp[0] = 1;
        for (int i = 0; i <= m; i++) {
            for (int j = i + 1; j <= m + 1; j++) {
                long len = marks[j] - marks[i];
                if (len > 0) {
                    boolean possible = true;
                    for (int k = i + 1; k < j; k++) {
                        if (marks[k] > marks[i] && marks[k] < marks[j]) {
                            possible = false;
                            break;
                        }
                    }
                    if (possible) {
                        long area = len * len;
                        dp[j] = (dp[j] + dp[i] * area) % MOD;
                    }
                }
            }
        }
        System.out.println(dp[m + 1]);
    }
}
