n, m = map(int, input().split())
mod = 10**9 + 7
if m == 0:
    print(pow(n, 2, mod))
else:
    x = list(map(int, input().split()))
    x = [0] + x + [n]
    dp = {}
    def solve(i, j):
        if (i, j) in dp:
            return dp[(i, j)]
        if i == m + 1:
            return 1
        ans = 0
        for k in range(j + 1, x[i + 1] + 1):
            ans = (ans + solve(i + 1, k) * (k - j) ** 2) % mod
        dp[(i, j)] = ans
        return ans
    print(solve(0, 0))

