#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int n;
    cin >> n;

    vector<pair<long long, long long>> x(n);
    for (int i = 0; i < n; ++i) {
        cin >> x[i].first >> x[i].second;
    }

    vector<long long> y(n + 1);
    for (int i = 0; i < n + 1; ++i) {
        cin >> y[i];
    }

    int q;
    cin >> q;

    for (int i = 0; i < q; ++i) {
        long long d, e;
        cin >> d >> e;

        vector<pair<long long, long long>> z = x;
        z.push_back({d, e});

        int max_score = -1;
        for (int j = 0; j < (1 << (n + 1)); ++j) {
            vector<long long> z_vals(n + 1);
            int score = 0;
            vector<bool> used_front(n + 1, false);
            bool possible = true;

            for (int k = 0; k <= n; ++k) {
                if ((j >> k) & 1) { 
                    z_vals[k] = z[k].first;
                    used_front[k] = true;
                    score++;
                } else {
                    z_vals[k] = z[k].second;
                }
            }

            vector<long long> sorted_z = z_vals;
            vector<long long> sorted_y = y;
            sort(sorted_z.begin(),sorted_z.end());
            sort(sorted_y.begin(),sorted_y.end());

            bool cur_possible = true;
            for(int k=0; k<=n; ++k){
                if(sorted_z[k] > sorted_y[k]){
                    cur_possible = false;
                    break;
                }
            }
            if(cur_possible){
                max_score = max(max_score, score);
            }
        }
        cout << max_score << endl;
    }

    return 0;
}
