import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[][] x = new int[n][2];
        for (int i = 0; i < n; i++) {
            x[i][0] = sc.nextInt();
            x[i][1] = sc.nextInt();
        }
        int[] c = new int[n + 1];
        for (int i = 0; i < n + 1; i++) {
            c[i] = sc.nextInt();
        }
        Arrays.sort(c);
        int q = sc.nextInt();
        for (int i = 0; i < q; i++) {
            int d = sc.nextInt();
            int e = sc.nextInt();
            int[][] z = new int[n + 1][2];
            for (int j = 0; j < n; j++) {
                z[j][0] = x[j][0];
                z[j][1] = x[j][1];
            }
            z[n][0] = d;
            z[n][1] = e;
            int maxScore = -1;
            for (int j = 0; j < (1 << (n + 1)); j++) {
                int score = 0;
                int[] used = new int[n + 1];
                List<Integer> usedC = new ArrayList<>();
                boolean possible = true;
                for (int k = 0; k < n + 1; k++) {
                    int val;
                    if ((j >> k) % 2 == 0) {
                        val = z[k][0];
                        score++;
                    } else {
                        val = z[k][1];
                    }
                    used[k] = val;
                }
                Arrays.sort(used);
                for (int k = 0; k < n + 1; k++) {
                    boolean found = false;
                    for (int l = 0; l < n + 1; l++) {
                        if (used[k] <= c[l] && !usedC.contains(l)) {
                            usedC.add(l);
                            found = true;
                            break;
                        }
                    }
                    if (!found) {
                        possible = false;
                        break;
                    }
                }
                if (possible) {
                    maxScore = Math.max(maxScore, score);
                }
            }
            System.out.println(maxScore);
        }
    }
}
