def solve():
    n = int(input())
    ab = []
    for _ in range(n):
        ab.append(list(map(int, input().split())))
    c = list(map(int, input().split()))
    q = int(input())

    for _ in range(q):
        d, e = map(int, input().split())
        z = ab + [[d, e]]

        max_score = -1

        for i in range(1 << (n + 1)):
            cur_z = []
            score = 0
            for j in range(n + 1):
                if (i >> j) & 1:
                    cur_z.append(z[j][0])
                    score += 1
                else:
                    cur_z.append(z[j][1])

            cur_c = sorted(c)
            cur_z = sorted(cur_z)

            possible = True
            for k in range(n+1):
                if cur_z[k] > cur_c[k]:
                    possible = False
                    break

            if possible:
                max_score = max(max_score, score)

        print(max_score)

solve()
