#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

bool match(const string& s, const string& p) {
  if (s.length() != p.length()) return false;
  for (size_t i = 0; i < s.length(); ++i) {
    if (p[i] != '?' && s[i] != p[i]) return false;
  }
  return true;
}

int main() {
  int n;
  cin >> n;
  vector<string> s(n);
  for (int i = 0; i < n; ++i) cin >> s[i];
  string t;
  cin >> t;

  vector<int> ans;
  vector<string> v = s;
  v.push_back(t);
  sort(v.begin(), v.end());

  vector<string> patterns;
  function<void(string, int)> generate = [&](string current, int k) {
    if (k == n) {
      patterns.push_back(current);
      return;
    }
    for (char c = 'a'; c <= 'z'; ++c) {
        string next = current;
        next += c;
        generate(next, k + 1);
    }
  };

    if (n > 0) {
        string current = "";
        generate(current,0);
    } else {
        patterns.push_back("");
    }

  for (int i = 0; i < patterns.size(); ++i) {
    vector<string> cur;
    bool possible = true;
    for (int j = 0; j < n; ++j) {
      if (!match(patterns[i].substr(j*t.length(), t.length()), s[j])) {
        possible = false;
        break;
      }
    }
    if (possible) {
        vector<string> cur_v = s;
        cur_v.push_back(t);

        for(int k = 0; k < n; ++k){
            string cur_s = patterns[i].substr(k*t.length(), t.length());
            cur_s.resize(cur_v[k].length());
            cur_v[k] = cur_s;
        }
        cur_v.push_back(t);

      sort(cur_v.begin(), cur_v.end());

      for (int j = 0; j < cur_v.size(); ++j) {
        if (cur_v[j] == t) ans.push_back(j + 1);
      }
    }
  }

  sort(ans.begin(), ans.end());
  ans.erase(unique(ans.begin(), ans.end()), ans.end());
  for (int i = 0; i < ans.size(); ++i) {
    cout << ans[i] << (i == ans.size() - 1 ? "" : " ");
  }
  cout << endl;
  return 0;
}
